// Function to render star rating
const renderStars = (count) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    if (i <= count) {
      stars.push(<span key={i} style={{ color: "#ffb400" }}>★</span>);
    } else {
      stars.push(<span key={i} style={{ color: "#ccc" }}>★</span>);
    }
  }
  return stars;
};

